import axios from 'axios';
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';

function AddEmployee() {
	const [data, setData] = useState({
		Idea: '',
		email: '',
		password: '',
		Description: '',

	})
	const navigate = useNavigate()			
	const [emailList, setEmailList] = useState('');
	const handleInputChange = (e) => {
		const inputValue = e.target.value;
		setEmailList(inputValue);
	  };
	  const handleSubmitemail = (e) => {
		e.preventDefault();
		const emails = emailList.split(',').map((email) => email.trim());
	
		// const invalidEmails = emails.filter((email) => !isValidEmail(email));
	
		// if (invalidEmails.length > 0) {
		//   setErrorMessage('Invalid email address(es): ' + invalidEmails.join(', '));
		// } else {
		//   setErrorMessage('');
		//   console.log('Valid email addresses:', emails);
		}

	const handleSubmit = (event) => {
		event.preventDefault();
		const formdata = new FormData();
		formdata.append("name", data.name);
		formdata.append("email", data.email);
		formdata.append("password", data.password);
		formdata.append("address", data.address)
		axios.post('http://localhost:8081/create', formdata)
		.then(res => {
			navigate('/employee')
		})
		.catch(err => console.log(err));
	}
	return (
		<div className='d-flex flex-column align-items-center pt-4'>
			<h2>Add New Ideas</h2>
			<form class="row g-3 w-50" onSubmit={handleSubmit}>
			<div class="col-12">
					<label for="inputName" class="form-label">Idea Name</label>
					<input type="text" class="form-control" id="inputName" placeholder='Enter Name' autoComplete='off'
					onChange={e => setData({...data, name: e.target.value})}/>
				</div>
				<div class="col-12" onSubmit={handleSubmitemail} value={emailList}
            onChange={handleInputChange}>
					<label for="inputEmail4" class="form-label">Email</label>
					
					<input type="email" class="form-control" id="inputEmail4" placeholder='abc@gmail.com' autoComplete='off'
					onChange={e => setData({...data, email: e.target.value})}/>
				</div>
				<div class="col-12">
					<label for="inputPassword4" class="form-label">Password</label>
					<input type="password" class="form-control" id="inputPassword4" placeholder='Enter Password'
					 onChange={e => setData({...data, password: e.target.value})}/>
				</div>
				
				<div class="col-12">
					<label for="inputAddress" class="form-label" rows="8">Description</label>
					<input type="text" class="form-control" id="inputAddress"  autoComplete='off' rows="8"
					onChange={e => setData({...data, address: e.target.value})}/>
				</div>
				<div class="col-12">
					<button type="submit" class="btn btn-primary">Submit</button>
				</div>
			</form>
		</div>

	)
}

export default AddEmployee
